4.7 Operations - Functions





form
code
live




(fn [x y] (return (+ x y)))
function (x,y){
  return x + y;
}
&lt;function&gt;
function (x,y){
    return x + y;
  }


(do (var hello (fn [x y] (return (+ x y)))) (hello 1 2))
let hello = function (x,y){
  return x + y;
};
hello(1,2);
3

4.8 Operations - Blocks

4.8.1 if block





form
code
live




(do (var arr [1 2 3 4 5]) (var out) (if (&lt; (x:len arr) 10) (:= out true) (:= out false)) out)
let arr = [1,2,3,4,5];
let out = null;
if(arr.length &lt; 10){
  out = true;
}
else{
  out = false;
}
out;
true







4.8.2 cond block





form
code
live




(do (var arr [1 2 3 4 5]) (var out) (cond (&lt; (x:len arr) 5) (:= out "1") (&lt; (x:len arr) 10) (:= out "2") :else (:= out "3")) out)
let arr = [1,2,3,4,5];
let out = null;
if(arr.length &lt; 5){
  out = "1";
}
else if(arr.length &lt; 10){
  out = "2";
}
else{
  out = "3";
}
out;
"2"







4.8.3 while block





form
code
live




(do (var x []) (var i 0) (while (&lt; i 5) (x:arr-push x i) (:++ i)) x)
let x = [];
let i = 0;
while(i &lt; 5){
  x.push(i);
  ++i;
}
x;
[0 1 2 3 4]







4.8.4 for block





form
code
live




(do (var arr []) (for [(var i 1) (&lt; i 5) (:++ i)] (x:arr-push arr i)) arr)
let arr = [];
for(let i = 1; i &lt; 5; ++i){
  arr.push(i);
}
arr;
[1 2 3 4]







4.8.5 case block





form
code
live




(do (var arr 1) (var out) (case arr 1 (do (:= out 1) (break)) 2 (do (:= out 2) (break))) out)
let arr = 1;
let out = null;
switch(arr){
  case 1:
    out = 1;
    break;

  case 2:
    out = 2;
    break;
}
out;
1







4.8.6 try/catch block





form
code
live




(do (var out "hello") (try (throw 1) (catch e (:= out "world"))) out)
let out = "hello";
try{
  throw 1;
}
catch(e){
  out = "world";
}
out;
"world"

5.1 Base Lib - For

5.1.1 for:array





form
code
live




(do (var out := []) (k/for:array [e [1 2 3 4]] (if (&gt; e 3) (break)) (x:arr-push out e)) out)
let out = [];
for(let e of [1,2,3,4]){
  if(e &gt; 3){
    break;
  }
  out.push(e);
};
out;
[1 2 3]







5.1.2 for:object





form
code
live




(do (var out := []) (var obj := {:a 1, :b 2}) (k/for:object [[k v] obj] (x:arr-push out [k v])) out)
let out = [];
let obj = {"a":1,"b":2};
for(let [k,v] of Object.entries(obj)){
  out.push([k,v]);
};
out;
[["a" 1] ["b" 2]]







5.1.3 for:index





form
code
live




(do (var out := []) (k/for:index [i [0 10 2]] (x:arr-push out i)) out)
let out = [];
for(let i = 0; i &lt; 10; i = (i + 2)){
  out.push(i);
};
out;
[0 2 4 6 8]







5.1.4 for:return





form
code
live




(do (var out) (var success (fn [cb] (cb nil "OK"))) (k/for:return [[ret err] (success (x:callback))] {:success (:= out ret), :error (:= out err)}) out)
let out = null;
let success = function (cb){
  cb(null,"OK");
};
success(function (err,ret){
  if(err){
    out = err;
  }
  else{
    out = ret;
  }
});
out;
"OK"







5.1.5 for:try





form
code
live




(do (var out := nil) (k/for:try [[ret err] (do:&gt; (x:err "hello"))] {:success (:= out ret), :error (:= out err)}) out)
let out = null;
try{
  let ret =   (function (){
      throw "hello";
    })();
  out = ret;
}
catch(err){
  out = err;
};
out;
"hello"







5.1.6 for:async





form
code
live




(do (var out := nil) (k/for:async [[ret err] (+ 1 2 3)] {:success (:= out ret), :error (:= out err)}))
let out = null;
new Promise(function (resolve,reject){
  resolve(1 + 2 + 3);
}).then(function (ret){
  out = ret;
}).catch(function (err){
  out = err;
});
&lt;Promise&gt;
[object Promise]

