5.2.8 x:shell





form
code
live




(do (var cb {}) (defn call [] (k/x:shell "ls" cb)) (call))
let cb = {};
function call(){
  let p = require("child_process");
  p.exec("ls",function (err,res){
    if(err){
      if(cb["error"]){
        return cb.error(err);
      }
    }
    else{
      if(cb["success"]){
        return cb.success(res);
      }
    }
  });
  return ["async"];;
}
call();
["async"]







